import React, { useState } from "react";
import { Container, Typography, Grid, Card, CardContent, CardMedia, IconButton, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, Button } from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import PeopleIcon from "@mui/icons-material/People";
import LibraryBooksIcon from "@mui/icons-material/LibraryBooks";
import BookIcon from "@mui/icons-material/Book";
import ImportContactsIcon from "@mui/icons-material/ImportContacts";
import { toast } from "react-toastify";

const LibrarianDashboard = () => {
  const [data, setData] = useState({
    usersCount: 0,
    sections: 0,
    ebooks: 0,
    totalBooksIssued: 0,
    users: [],
  });
  const [open, setOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);

  const handleDeleteClick = (user) => {
    setSelectedUser(user);
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    setSelectedUser(null);
  };

  const handleDeleteConfirm = () => {
    // Simulating a delete action without database interaction
    setData((prevState) => ({
      ...prevState,
      users: prevState.users.filter((user) => user._id !== selectedUser._id),
    }));
    toast.success("User deleted successfully");
    handleClose();
  };

  return (
    <Container sx={{ paddingTop: 5 }}>
      <Grid container spacing={3} sx={{ marginBottom: 5 }}>
        <Grid item xs={12} sm={6} md={3}>
          <Card
            sx={{
              display: "flex",
              alignItems: "center",
              backgroundColor: "#eb348f",
              color: "#fff",
              height: 150,
              transition: "transform 0.3s ease-in-out",
              "&:hover": {
                transform: "scale(1.05)",
              },
            }}
          >
            <CardMedia>
              <PeopleIcon sx={{ fontSize: 50, margin: 2 }} />
            </CardMedia>
            <CardContent>
              <Typography variant="h6">Active Users</Typography>
              <Typography variant="h4">{data.usersCount}</Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card
            sx={{
              display: "flex",
              alignItems: "center",
              backgroundColor: "#51a9ed",
              color: "#fff",
              height: 150,
              transition: "transform 0.3s ease-in-out",
              "&:hover": {
                transform: "scale(1.05)",
              },
            }}
          >
            <CardMedia>
              <LibraryBooksIcon sx={{ fontSize: 50, margin: 2 }} />
            </CardMedia>
            <CardContent>
              <Typography variant="h6">Total Sections</Typography>
              <Typography variant="h4">{data.sections}</Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card
            sx={{
              display: "flex",
              alignItems: "center",
              backgroundColor: "#75e354",
              color: "#fff",
              height: 150,
              transition: "transform 0.3s ease-in-out",
              "&:hover": {
                transform: "scale(1.05)",
              },
            }}
          >
            <CardMedia>
              <BookIcon sx={{ fontSize: 50, margin: 2 }} />
            </CardMedia>
            <CardContent>
              <Typography variant="h6">Total E-books</Typography>
              <Typography variant="h4">{data.ebooks}</Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card
            sx={{
              display: "flex",
              alignItems: "center",
              backgroundColor: "#FF9800",
              color: "#fff",
              height: 150,
              transition: "transform 0.3s ease-in-out",
              "&:hover": {
                transform: "scale(1.05)",
              },
            }}
          >
            <CardMedia>
              <ImportContactsIcon sx={{ fontSize: 50, margin: 2 }} />
            </CardMedia>
            <CardContent>
              <Typography variant="h6">Total Books Issued</Typography>
              <Typography variant="h4">{data.totalBooksIssued}</Typography>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
      <Typography variant="h6" gutterBottom sx={{ paddingTop: 5 }}>
        All Users
      </Typography>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell sx={{ fontWeight: "bold" }}>Username</TableCell>
            <TableCell sx={{ fontWeight: "bold" }}>Email</TableCell>
            <TableCell sx={{ fontWeight: "bold" }}>Role</TableCell>
            <TableCell sx={{ fontWeight: "bold" }}>Action</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {data.users.map((user) => (
            <TableRow key={user._id}>
              <TableCell>{user.username}</TableCell>
              <TableCell>{user.email}</TableCell>
              <TableCell>{user.role}</TableCell>
              <TableCell>
                <IconButton
                  color="error"
                  onClick={() => handleDeleteClick(user)}
                >
                  <DeleteIcon />
                </IconButton>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>

      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">{"Delete User"}</DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            Are you sure you want to delete this user?
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={handleDeleteConfirm} color="error" autoFocus>
            Confirm
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default LibrarianDashboard;
