const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const { role, dispatch } = useContext(AppContext);
  const navigate = useNavigate();

  useEffect(() => {
    if (role) {
      // Navigate to dashboard or libraries after login
      navigate("/libraries");  // Change to '/librarian-dashboard' or other route if needed
    }
  }, [role, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Mocking login success for demonstration
    const mockResponse = {
      data: {
        token: "mock_token",
        user: {
          role: "user",  // Change based on your actual user role logic
        }
      }
    };

    // Dispatch login success with user role and token
    dispatch({
      type: "LOGIN_SUCCESS",
      payload: { token: mockResponse.data.token, role: mockResponse.data.user.role },
    });

    // Navigate directly to libraries or dashboard after successful login
    navigate("/libraries");  // Or '/librarian-dashboard'

    // Show success toast
    toast.success("Login successful!");
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        {/* Form content */}
      </form>
    </div>
  );
};
